import { useState, useEffect } from 'react';

const focusEnum = [
  "Main Focus",
  "Secondary Focus",
  "Mentioned in Future Work",
  "Not Mentioned",
  "Mentioned but not Elaborated",
  "Mentioned not Elab"
];

const dataProcessedEnum = ["automatic", "semi-automatic", "manual"];
const natureOfDataEnum = ["synthetic", "real-world"];
const complianceLevelEnum = ["Compliance Level", "Compliance Degree", "Both"];

const PaperUpdate = ({ paper }) => {
  const [form, setForm] = useState({});

  // When paper prop is available, populate the form state
  useEffect(() => {
    if (paper) {
      setForm({ ...paper });
    }
  }, [paper]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Submitting:', form);

    try {
      const res = await fetch(`/api/papers/${paper._id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });

      if (!res.ok) {
        const errText = await res.text();
        throw new Error(errText);
      }

      alert('Paper updated!');
    } catch (err) {
      alert('Error: ' + err.message);
    }
  };

  const renderSelect = (label, name, options) => (
    <div>
      <label><strong>{label}</strong></label><br />
      <select name={name} value={form[name] || ''} onChange={handleChange}>
        <option value="">-</option>
        {options.map((opt) => (
          <option key={opt} value={opt}>{opt}</option>
        ))}
      </select>
    </div>
  );

  return (
    <form onSubmit={handleSubmit} style={{ display: 'grid', gap: '1rem' }}>
      <h2>Update Paper</h2>

      <input type="number" name="year" placeholder="Year" value={form.year || ''} onChange={handleChange} />
      <input type="text" name="title" placeholder="Title" value={form.title || ''} onChange={handleChange} />
      <input type="number" name="numberOfCitations" placeholder="Number of Citations" value={form.numberOfCitations || ''} onChange={handleChange} />
      <textarea name="abstract" placeholder="Abstract" value={form.abstract || ''} onChange={handleChange} />
      <input type="text" name="doi" placeholder="DOI" value={form.doi || ''} onChange={handleChange} />
      <input type="text" name="typeOfPaper" placeholder="Type of Paper" value={form.typeOfPaper || ''} onChange={handleChange} />
      <label>
        <input type="checkbox" name="dataAccessible" checked={form.dataAccessible || false} onChange={handleChange} />
        Data Accessible
      </label>

      {/* Enum Fields */}
      {renderSelect("Modeling", "BPC_Task_ComplianceElicitation_Modeling", focusEnum)}
      {renderSelect("Extraction", "BPC_Task_ComplianceElicitation_Extraction", focusEnum)}
      {renderSelect("Verification", "BPC_Task_ComplianceChecking_Verification", focusEnum)}
      {renderSelect("Monitoring", "BPC_Task_ComplianceChecking_EnforcementMonitoring", focusEnum)}
      {renderSelect("Audit", "BPC_Task_ComplianceChecking_Audit", focusEnum)}
      {renderSelect("Reporting", "BPC_Task_ComplianceAnalysis_Reporting", focusEnum)}
      {renderSelect("Explanation", "BPC_Task_ComplianceAnalysis_Explanation", focusEnum)}
      {renderSelect("Recovery", "BPC_Task_ComplianceEnhancement_Recovery", focusEnum)}
      {renderSelect("Resolution", "BPC_Task_ComplianceEnhancement_Resolution", focusEnum)}

      <input type="text" name="BPC_Task_Others" placeholder="Other BPC Task" value={form.BPC_Task_Others || ''} onChange={handleChange} />

      {/* Type of Data */}
      <input type="text" name="TypeOfData_RegulatoryDocuments" placeholder="Regulatory Documents" value={form.TypeOfData_RegulatoryDocuments || ''} onChange={handleChange} />
      <input type="text" name="TypeOfData_PureTextRequirements" placeholder="Pure Text Requirements" value={form.TypeOfData_PureTextRequirements || ''} onChange={handleChange} />
      <input type="text" name="TypeOfData_InternalPolicies" placeholder="Internal Policies" value={form.TypeOfData_InternalPolicies || ''} onChange={handleChange} />
      <input type="text" name="TypeOfData_BPModels" placeholder="BP Models" value={form.TypeOfData_BPModels || ''} onChange={handleChange} />
      <input type="text" name="TypeOfData_BPDescription" placeholder="BP Description" value={form.TypeOfData_BPDescription || ''} onChange={handleChange} />
      <input type="text" name="TypeOfData_EventLogs" placeholder="Event Logs" value={form.TypeOfData_EventLogs || ''} onChange={handleChange} />
      <input type="text" name="TypeOfData_FormalizedConstraints" placeholder="Formalized Constraints" value={form.TypeOfData_FormalizedConstraints || ''} onChange={handleChange} />
      <input type="text" name="TypeOfData_SemiformalizedConstraints" placeholder="Semiformalized Constraints" value={form.TypeOfData_SemiformalizedConstraints || ''} onChange={handleChange} />
      <input type="text" name="TypeOfData_Others" placeholder="Other Type of Data" value={form.TypeOfData_Others || ''} onChange={handleChange} />

      {/* FAQ */}
      <input type="text" name="FAQ_OtherDataInFuture" placeholder="Future Data Plans" value={form.FAQ_OtherDataInFuture || ''} onChange={handleChange} />
      {renderSelect("How was the data processed?", "FAQ_DataProcessed", dataProcessedEnum)}
      <input type="text" name="FAQ_DataConverter" placeholder="Data Converter" value={form.FAQ_DataConverter || ''} onChange={handleChange} />
      <input type="text" name="FAQ_LimitationsOfDataset" placeholder="Limitations" value={form.FAQ_LimitationsOfDataset || ''} onChange={handleChange} />
      {renderSelect("Nature of Data", "FAQ_NatureOfData", natureOfDataEnum)}
      <input type="text" name="FAQ_MoreThanOneVersion" placeholder="Multiple Versions?" value={form.FAQ_MoreThanOneVersion || ''} onChange={handleChange} />
      {renderSelect("Compliance Level or Degree", "FAQ_ComplianceLevelOrDegree", complianceLevelEnum)}
      <input type="text" name="FAQ_Stakeholders" placeholder="Stakeholders" value={form.FAQ_Stakeholders || ''} onChange={handleChange} />

      <button type="submit">Update Paper</button>
    </form>
  );
};

export default PaperUpdate;
