const Filter = ({
    startYear,
    endYear,
    modeling,
    extraction,
    verification,
    monitoring,
    audit,
    reporting,
    explanation,
    recovery,
    resolution,
    setStartYear,
    setEndYear,
    setModeling,
    setExtraction,
    setVerification,
    setMonitoring,
    setAudit,
    setReporting,
    setExplanation,
    setRecovery,
    setResolution,
  }) => {
    const renderCheckbox = (label, value, setter) => (
      <div className="flex items-center space-x-2">
        <input
          type="checkbox"
          id={label}
          checked={value}
          onChange={(e) => setter(e.target.checked)}
          className="w-4 h-4"
        />
        <label htmlFor={label} className="font-medium">{label}</label>
      </div>
    );
  
    return (
      <div className="filter_container space-y-4 p-4 bg-gray-100 rounded-xl">
        {/* Year Range */}
        <div>
          <label className="block font-semibold">Start Year</label>
          <input
            type="number"
            value={startYear ?? ""}
            onChange={(e) => setStartYear(Number(e.target.value))}
            className="border rounded px-2 py-1 w-full"
          />
        </div>
        <div>
          <label className="block font-semibold">End Year</label>
          <input
            type="number"
            value={endYear ?? ""}
            onChange={(e) => setEndYear(Number(e.target.value))}
            className="border rounded px-2 py-1 w-full"
          />
        </div>
  
        {/* Boolean Checkboxes (controlled) */}
        <div className="space-y-2">
          {renderCheckbox("Modeling", modeling, setModeling)}
          {renderCheckbox("Extraction", extraction, setExtraction)}
          {renderCheckbox("Verification", verification, setVerification)}
          {renderCheckbox("Monitoring", monitoring, setMonitoring)}
          {renderCheckbox("Audit", audit, setAudit)}
          {renderCheckbox("Reporting", reporting, setReporting)}
          {renderCheckbox("Explanation", explanation, setExplanation)}
          {renderCheckbox("Recovery", recovery, setRecovery)}
          {renderCheckbox("Resolution", resolution, setResolution)}
        </div>
      </div>
    );
  };
  
  export default Filter;
