const focusEnum = [
    "Main Focus",
    "Secondary Focus",
    "Mentioned in Future Work",
    "Not Mentioned",
    "Mentioned but not Elaborated"
  ];
  
  const Filter = ({
    setStartYear,
    setEndYear,
    setModeling,
    setExtraction,
    setVerification,
    setMonitoring,
    setAudit,
    setReporting,
    setExplanation,
    setRecovery,
    setResolution,
  }) => {
    const renderFocusDropdown = (label, onChangeHandler) => (
      <div>
        <label className="block font-semibold">{label}</label>
        <select
          onChange={(e) => onChangeHandler(e.target.value)}
          className="border rounded px-2 py-1 w-full"
        >
          <option value="">Select...</option>
          {focusEnum.map((option) => (
            <option key={option} value={option}>{option}</option>
          ))}
        </select>
      </div>
    );
  
    return (
      <div className="filter_container space-y-4 p-4 bg-gray-100 rounded-xl">
        {/* Year Range */}
        <div>
          <label className="block font-semibold">Start Year</label>
          <input
            type="number"
            onChange={(e) => setStartYear(Number(e.target.value))}
            className="border rounded px-2 py-1 w-full"
          />
        </div>
        <div>
          <label className="block font-semibold">End Year</label>
          <input
            type="number"
            onChange={(e) => setEndYear(Number(e.target.value))}
            className="border rounded px-2 py-1 w-full"
          />
        </div>
  
        {/* BPC Task Compliance Dropdowns with correct enum */}
        {renderFocusDropdown("Modeling", setModeling)}
        {renderFocusDropdown("Extraction", setExtraction)}
        {renderFocusDropdown("Verification", setVerification)}
        {renderFocusDropdown("Monitoring", setMonitoring)}
        {renderFocusDropdown("Audit", setAudit)}
        {renderFocusDropdown("Reporting", setReporting)}
        {renderFocusDropdown("Explanation", setExplanation)}
        {renderFocusDropdown("Recovery", setRecovery)}
        {renderFocusDropdown("Resolution", setResolution)}
      </div>
    );
  };
  
  export default Filter;
